head.script:
var lCookie = "ko";
var sCookie = document.cookie;
if (sCookie.length >= 1){
	var rReg = new RegExp('\\bDICT_LOCALE2=([^;]*)(;|$)');
	var tmpCokie = sCookie.match(rReg);
	if(tmpCokie!=null){
		lCookie = tmpCokie && tmpCokie[1];
	} else{
		var bLang = "ko";
		if(bLang=="zh_CN" || bLang=="ja" || bLang=="en" || bLang=="vi" || bLang=="th" || bLang=="id"){
			lCookie = bLang;
		} else {
			lCookie = "ko";
		}
	}
} else{
	var bLang = "ko";
	if(bLang=="zh_CN" || bLang=="ja" || bLang=="en" || bLang=="vi" || bLang=="th" || bLang=="in"){
		lCookie = bLang;
	} else {
		lCookie = "ko";
	}
}

var bClass = "";
if(lCookie == "ko"){
	bClass = "";
} else {
	bClass = "skin";
}

