//iframe domready not work problem
String.prototype.trim = function(){
  return this.replace(/^\s*|$\s*/,"");
}
function clearButtonAndfocus(){
	if(document.getElementById("main_input")){
		var inputId = document.getElementById("main_input");
		if(inputId && inputId.value) {
			inputId.value = "";
		}
		inputId.focus();
		$Element(inputId).fireEvent("focus");
	}
}
function emptyInputBox(){
	if($Agent().navigator().ie && window.top != window){
		var inputId = document.getElementById("main_input");
		if(inputId && inputId.value)
		 	inputId.value = "";
	}else{
		if(document.getElementById("main_input")){
			var inputId = document.getElementById("main_input");
			if(inputId && inputId.value)
			 	inputId.value = "";
		}
		if(document.getElementById("sub_input")){
			var subinputId = document.getElementById("sub_input");
			if(subinputId && subinputId.value)
			 	subinputId.value = "";
		}
	}
}

