			jQuery("#vliveHelpClsBtn").on("click", function(){
				jQuery("#vliveHelpWrap").hide();
			});
			//  v 팬자막 예문 컬렉션 접기/펼치기
			if (jQuery("#openVliveExamCollection").val() != "true") {
				if (jQuery.cookie("VLIVE_COLLECTION_OPEN_YN") == "N") {
					vliveCollectionHide();
				} else {
					vliveCollectionShow();
				}
			} 
			// v 팬자막 예문 컬렉션 접기/펼치기 이벤트 바인딩
			jQuery("#vliveTogglenBtn").on("click", function(){
				if (jQuery.cookie("VLIVE_COLLECTION_OPEN_YN") == "N") {
					vliveCollectionShow();
					jQuery.cookie("VLIVE_COLLECTION_OPEN_YN", "Y", {expires:365, domain: '.endic.naver.com', path:'/'});
				} else {
					vliveCollectionHide();
					jQuery.cookie("VLIVE_COLLECTION_OPEN_YN", "N", {expires:365, domain: '.endic.naver.com', path:'/'});
				}
			});
			// v 팬자막 예문 컬렉션 노출 
			function vliveCollectionShow(){
				nclk(this, 'xmp.unfold', '', 1);
				jQuery("#vliveExamCollection").removeClass("folded");
				jQuery("#vliveTogglenBtn").html(window.i18n("endic.vlive.example.fold", "접기"));
				jQuery("#vliveTogglenBtn").show();
			}
			// v 팬자막 예문 컬렉션 비노출 
			function vliveCollectionHide(){
				nclk(this, 'xmp.fold', '', 1);
				jQuery("#vliveExamCollection").addClass("folded");
				jQuery("#vliveTogglenBtn").html(window.i18n("endic.vlive.example.unfold", "펼치기"));
				jQuery("#vliveTogglenBtn").show();
			}
			// 툴팁 API
