}

var cLocaleCokie = "";
//쿠키를 받는다 .
var dCookie = document.cookie;
if (dCookie.length >= 1){
	var rReg = new RegExp('\\bDICT_LOCALE2=([^;]*)(;|$)');
	var tCokie = dCookie.match(rReg);
	if(tCokie!=null){
		cLocaleCokie = tCokie && tCokie[1];
		if(cLocaleCokie == "ko"){
			cLocaleCokie="";
		}else if(cLocaleCokie == "zh_CN"){
			cLocaleCokie="zhcn";
		}
	}
}

if(cLocaleCokie == ""){
	cLocaleCokie = window.i18n("locale.locale","ko");
	if(cLocaleCokie == "ko"){
		cLocaleCokie="";
	}else if(cLocaleCokie == "zh_CN"){
		cLocaleCokie="zhcn";
	}else if(cLocaleCokie == "in"){
		cLocaleCokie="id";
	}
}

if(cLocaleCokie != ""){
	cLocaleCokie="_"+cLocaleCokie;
}

