jQuery(window).load(function(){
	var html = jQuery("html")[0];
	var lang = "ko";
	// 쿠키에서 DICT_LOCALE2의 값을 읽어 lang값으로 부여함.
	var cookie = document.cookie;

	if (cookie.length >= 1){
		var rReg = new RegExp('\\bDICT_LOCALE2=([^;]*)(;|$)');
		var tmpCokie = cookie.match(rReg);
		if(tmpCokie != null && tmpCokie[1] != ""){
			lang = tmpCokie[1] == "zh_CN" ? "zh" : tmpCokie[1];
		}
	}

	jQuery(html).attr("lang", lang);
});

