jindo.$Fn(function(){
if("Y" == 'N'){
	//번역기 결과 노출
	var transCon = "\"discuss\"";
	transCon = transCon.replace(/\\\"/g,"\"");
	var sLang = checkLanguage(transCon)==2?"ko":"en";
	var tLang = sLang=="ko"?"en":"ko";
	var transTitleDiv = $$.getSingle('div.wordTransTitle');
	var transResultSection = $$.getSingle('section.wordTransResult');
	var transLinkDiv = $$.getSingle('div.wordTransLink');
	if(transResultSection==null) return;
	var ajax = new $Ajax("ajaxTransResult.nhn", {		
		type: "jsonp",
		method: "post",
		onload: function(res) {
			var transResult = res.json();						
			if (transResult.resultCode == 0 && transResult.resultData!="") {
				var transUrl = "http://translate.naver.com/#form_endic/" + sLang + "/" + tLang + "/" + encodeURIComponent(transCon);
				// [2016 사내취약점제보] 모바일웹영어사전 - Reflected XSS
				var escapeResultData = decodeURIComponent(transResult.resultData);
				if (escapeResultData.indexOf("<") > -1 || escapeResultData.indexOf(">") > -1){
		        	escapeResultData = escapeResultData.replace(//g, ">");
		        }
				$Element($$.getSingle('div.entry_trans a.trans_wrap span.trans_mean', transResultSection)).html(escapeResultData);
				$Element($$.getSingle('div.entry_trans a.trans_wrap', transResultSection)).attr("href", transUrl);
				$Element($$.getSingle('button.btn_more_show', transLinkDiv)).attr("onClick", "window.open(\""+transUrl+"\")");
              	if(sLang=="ko"){
              		$Element($$.getSingle('div.entry_trans a.trans_wrap', transResultSection)).addClass("kr");
              	}else{
              		$Element($$.getSingle('div.entry_trans a.trans_wrap', transResultSection)).addClass("en");
              	}
              	$Element(transTitleDiv).show();
              	transResultSection.style.display="block";
              	$Element(transLinkDiv).show();  
			}
		}
	}).request({
		"m": "getTransResult",
        "query": encodeURIComponent(transCon),         
		"sl": sLang,
		"tl": tLang
	});
}			
}).attach(document, 'domready');

window.onscroll = function () { 
	// iOS 사전앱일 경우 검색창 상단고정 않함.
	if (navigator.userAgent.indexOf("naverdicapp") > -1 && (navigator.userAgent.indexOf("iPad") > -1 || navigator.userAgent.indexOf("iPhone") > -1)){
		stopSearchFollow = true;
	} else if ($Element("nDrawer").css("display") == "none") { // 햄버거 메뉴 닫긴 경우
		stopSearchFollow = false;
	}

	if(!stopSearchFollow){
		var a = document.documentElement.scrollTop==0? document.body.clientHeight : document.documentElement.clientHeight;
		var b = document.documentElement.scrollTop==0? document.body.scrollTop : document.documentElement.scrollTop;
		var c = document.documentElement.scrollTop==0? document.body.scrollHeight : document.documentElement.scrollHeight;
		if (b > 45 && c-(a+b) > 200) {
			$Element($("header")).addClass("is_fixed");
			$Element("wrap").css("padding-top", "48px");
			if($("main_input")){
	        	$("main_input").blur();
			}
			if($("footerToTop")){$Element($("footerToTop")).show();}
		}
		else { 
			if(c-(a+b) > 200){
				$Element($("header")).removeClass("is_fixed");
				$Element("wrap").css("padding-top", "");
			}
			if($("footerToTop")){$Element($("footerToTop")).hide();}
		}
	}else{
		if($Element($("header")).hasClass("is_fixed")){
			$Element($("header")).removeClass("is_fixed");
		}
	}
}
